class Ui_remocao(object):

    def valida(self, codigo, quantidade):
        data = sqlite3.connect("inventory.db")
        cur = data.cursor()

        if quantidade and codigo:
            cur.execute("SELECT Qtd FROM Estoque WHERE Cod_prod = :cod", {'cod': int(codigo)})
            prodQtd = cur.fetchone()
            if int(quantidade) > prodQtd[0]:
                return False
        return True

    def loadData(self):
        data = sqlite3.connect("inventory.db")
        cur = data.cursor()
        cur.execute("SELECT COUNT() FROM Estoque")
        count = cur.fetchone()
        comando = "SELECT e.Cod_prod, p.Nome, c.Nome, e.Qtd FROM Estoque as e " \
                  "LEFT JOIN Produtos P on e.Cod_prod = P.Cod " \
                  "LEFT JOIN Categoria C on P.Categoria = C.Cod"

        self.tableWidget.setRowCount(count[0])
        tableIndex = 0
        for row in cur.execute(comando):
            self.tableWidget.setItem(tableIndex, 0, QtWidgets.QTableWidgetItem(str(row[0])))
            self.tableWidget.setItem(tableIndex, 1, QtWidgets.QTableWidgetItem(row[1]))
            self.tableWidget.setItem(tableIndex, 2, QtWidgets.QTableWidgetItem(row[2]))
            self.tableWidget.setItem(tableIndex, 3, QtWidgets.QTableWidgetItem(str(row[3])))
            tableIndex += 1
        data.close()

    def retira(self):
        data = sqlite3.connect("inventory.db")
        cur = data.cursor()

        cod = self.Codigo.text()
        qtd = self.Qtd.text()
        count = cur.execute("SELECT COUNT() FROM Movimentacao").fetchone()

        if cod and qtd:
            if self.valida(int(cod), int(qtd)):
                cur.execute("INSERT INTO Movimentacao VALUES(NULL,'S',:cod,:qtd, DATE())",
                            {'cod': int(cod), 'qtd': int(qtd)})
                data.commit()
            else:
                print("Quantidade invalida")
        data.close()
        self.loadData()

